Ext.define('Rai.view.frmServiziViewController4', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.frmserviziadmin',

    onSave: function(button, e, eOpts) {
        var descrizione = Ext.getCmp('descrizioneTxt').getValue(),
            importo = Ext.getCmp('importoTxt').getValue(),
            note = Ext.getCmp('noteServiziTxt').getValue(),
            fornitore = Ext.getCmp('fornitoreCombo').getValue(),
            tipologia = Ext.getCmp('tipologiaCombo').getValue();

        if (Ext.getCmp('ordinarioCheck').checked)tipo = "ordinario";
        else if ( Ext.getCmp('straordinarioCheck').checked)tipo = "straordinario";
        else if ( Ext.getCmp('corpoCheck').checked)tipo = "corpo";

        var store = Ext.StoreManager.lookup('storeServizi');
        var emptyApp = new Rai.model.servizi();
        emptyApp.set('descrizione',descrizione);
        emptyApp.set('importo',importo);
        emptyApp.set('fornitore',fornitore);
        emptyApp.set('tipo',tipo);
        emptyApp.set('tipologia',tipologia);
        emptyApp.set('note',note);
        store.add(emptyApp);
        store.on('write', function() { store.load();});
    },

    onDelete: function(button, e, eOpts) {
        var selection = Ext.getCmp('serviziGrid').getSelectionModel().getSelection();
        var store = Ext.StoreManager.lookup('storeServizi');
        store.remove(selection);
    },

    onClear: function(button, e, eOpts) {
        Ext.getCmp('descrizioneTxt').setValue(""),
        Ext.getCmp('importoTxt').setValue(""),
        Ext.getCmp('noteServiziTxt').setValue(""),
        fornitore = Ext.getCmp('fornitoreCombo').setValue("");

        Ext.getCmp('ordinarioCheck').setValue(false);
        Ext.getCmp('straordinarioCheck').setValue(false);
        Ext.getCmp('corpoCheck').setValue(false);
    }

});
